angular.module("my-pharma-pref-js",[]).service("my-pharma-pref-service",function(e){this.endpoint="http://localhost:8089/api/prefs",this.setEndPoint=function(e){this.endpoint=e},this.getPreferences=function(n,t){console.log("get all preferences."),e.get(endpoint+"/preferences").then(n,t)},this.createPreference=function(n,t,r,o){console.log("create preference.");var c={key:n,value:t};e.post(endpoint+"/preference",c).then(r,o)},this.createPreference=function(n,t,r){console.log("Find preference by key."),e.get(endpoint+"/preference/"+n).then(t,r)}});